let output = $input.first().json.output;
// Remove markdown code blocks if present
output = output.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
const parsed = JSON.parse(output);
return [{ json: parsed }];

const data = $input.first().json;
const type = data.type || 'paca';
const endpoint = data.endpoint;
const params = data.params || {};

let url;
if (type === 'university') {
  url = `https://supermax.kr/maxai/api/universities/${endpoint}`;
} else {
  url = `https://supermax.kr/maxai/api/paca/${endpoint}`;
}

// Build query string
const queryParams = Object.entries(params)
  .filter(([k, v]) => v !== undefined && v !== null && v !== '')
  .map(([k, v]) => `${k}=${encodeURIComponent(v)}`)
  .join('&');

if (queryParams) {
  url += '?' + queryParams;
}

return [{ json: { url, params } }];

